response.code:
Command command = Game.currentCommand();
GameObject currentRoom = Game.getCurrentRoom();
GameObject item = currentRoom.get(command.getObject());
GameObject player = Game.player();
if ((item != null) &&
    (item.getVar("movable").boolValue()))
{
   currentRoom.remove(item);
   player.add(item);

   Game.echo("You added a %s to your inventory.", item.getName());
}
